mapper.select:

	select no, user_no as userno, title, content, hit, g_no as gno, o_no as ono, depth
	from board
	where no=#{no }

mapper.update:

	update board as b inner join (select a.no 'no', a.o_no+1 'num' 
								from(select * 
									from board 
									where no != #{no } and g_no = #{gno } and depth = #{depth }) a) as a on (b.no=a.no)
			set b.o_no=a.num 
			where b.no != #{no }
			and b.g_no = #{gno }
			and b.depth = #{depth }
